mapper.select:
SELECT * FROM boardview 
WHERE board_no=#{bno } 
	
	AND b_no=(SELECT b_no FROM big_user WHERE b_id=#{id })

SELECT * FROM board2view 
WHERE board_no=#{bno } 
	
	AND b_no=(SELECT b_no FROM big_user WHERE b_id=#{id })

SELECT c.*, u.b_id, u.b_name
FROM comment c JOIN big_user u ON c.b_no=u.b_no
WHERE c.board_no=#{bno }
